import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch data from your server, assuming your server is running on http://localhost:5000
    axios.get('github.com/the-hideout/tarkov-api')
      .then(response => {
        setItems(response.data.items);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []); // The empty dependency array ensures that the effect runs once when the component mounts

  return (
    <div>
      <h1>Tarkov Items</h1>
      <ul>
        {items.map(item => (
          <div key={item.id}>
            <h3>{item.name}</h3>
            <p>ID: {item.id}</p>
            <p>Base Price: {item.basePrice}</p>
            <p>Avg 24h Price: {item.avg24hPrice}</p>
            <img src={item.gridImageLink} alt={item.name} />
            <a href={item.wikiLink} target="_blank" rel="noopener noreferrer">
              Wiki Link
            </a>
            <ul>
              {item.usedInTasks.map(task => (
                <li key={task.id}>
                  Task: {task.name}, Min Player Level: {task.minPlayerLevel}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
