import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Your GraphQL query
        const query = `
          {
            items {
              id
              name
              basePrice
              avg24hPrice
              gridImageLink
              inspectImageLink
              usedInTasks {
                id
                name
                minPlayerLevel
              }
              wikiLink
            }
          }
        `;

        // Make a POST request to the GraphQL endpoint
        const response = await axios.post('https://tarkov-api.now.sh/graphql', { query });

        // Extract the data from the response
        const responseData = response.data.data;

        setItems(responseData.items);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Call the fetchData function
    fetchData();
  }, []); // The empty dependency array ensures that the effect runs once when the component mounts

  return (
    <div>
      <h1>Tarkov Items</h1>
      <ul>
        {items.map(item => (
          <div key={item.id}>
            <h3>{item.name}</h3>
            <p>ID: {item.id}</p>
            <p>Base Price: {item.basePrice}</p>
            <p>Avg 24h Price: {item.avg24hPrice}</p>
            {/* Add more information as needed */}
            <img src={item.gridImageLink} alt={item.name} />
            <a href={item.wikiLink} target="_blank" rel="noopener noreferrer">
              Wiki Link
            </a>
            <ul>
              {item.usedInTasks.map(task => (
                <li key={task.id}>
                  Task: {task.name}, Min Player Level: {task.minPlayerLevel}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
