import React, { useState, useEffect } from 'react';
import { request, gql } from 'graphql-request';
import './App.css'; // Import a CSS file for styling (create App.css in the same directory as App.js)

function App() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Your GraphQL query
        const query = gql`
          {
            items {
              id
              name
              basePrice
              avg24hPrice
              gridImageLink
              inspectImageLink
              usedInTasks {
                id
                name
                minPlayerLevel
              }
              wikiLink
            }
          }
        `;

        // Make a request to the GraphQL endpoint
        const data = await request('https://api.tarkov.dev/graphql', query);

        setItems(data.items);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    // Call the fetchData function
    fetchData();
  }, []); // The empty dependency array ensures that the effect runs once when the component mounts

  return (
    <div className="app-container">
      <h1 className="app-title">Tarkov Items</h1>
      <ul className="item-list">
        {items.map(item => (
          <div key={item.id} className="item-card">
            <h3>{item.name}</h3>
            <p>ID: {item.id}</p>
            <p>Base Price: {item.basePrice}</p>
            <p>Avg 24h Price: {item.avg24hPrice}</p>
            <img src={item.gridImageLink} alt={item.name} className="item-image" />
            <a href={item.wikiLink} target="_blank" rel="noopener noreferrer" className="wiki-link">
              Wiki Link
            </a>
            <ul className="task-list">
              {item.usedInTasks.map(task => (
                <li key={task.id}>
                  Task: {task.name}, Min Player Level: {task.minPlayerLevel}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default App;
