import React, { useState, useEffect } from "react";
import { request, gql } from "graphql-request";
import "./App.css";

function App() {
	const [items, setItems] = useState([]);
	const [searchTerm, setSearchTerm] = useState("");

	useEffect(() => {
		const fetchData = async () => {
			try {
				const query = gql`
					{
						items {
							id
							name
							changeLast48h
							sellFor {
								price
								currency
								priceRUB
								source
							}
							buyFor {
								price
								currency
								priceRUB
								source
							}
							lastLowPrice
							inspectImageLink
							usedInTasks {
								id
								name
								minPlayerLevel
							}
							wikiLink
						}
					}
				`;

				const data = await request("https://api.tarkov.dev/graphql", query);

				setItems(data.items);
			} catch (error) {
				console.error("Error fetching data:", error);
			}
		};

		fetchData();
	}, []);

	const handleSearch = () => {
		const searchTermLowerCase = searchTerm.toLowerCase();
		const filteredItems = items.filter((item) =>
			item.name.toLowerCase().includes(searchTermLowerCase)
		);
		setItems(filteredItems);
	};

	const handleKeyPress = (event) => {
		if (event.key === "Enter") {
			handleSearch();
		}
	};

	return (
		<div className="app-container">
			<div className="search-container">
				<input
					type="text"
					placeholder="Search for an item"
					value={searchTerm}
					onChange={(e) => setSearchTerm(e.target.value)}
					onKeyPress={handleKeyPress}
				/>
				<button onClick={handleSearch}>Search</button>
			</div>
			<h1 className="app-title">Tarkov Items</h1>
			<ul className="item-list">
				{items.map((item) => (
					<div key={item.id} className="item-card">
						<h3>{item.name}</h3>
						<p>ID: {item.id}</p>
						<p>Change Last 48h: {item.changeLast48h}</p>
						{item.sellFor && item.sellFor.length > 0 && (
							<>
								<p>Sell For:</p>
								<ul className="price-list">
									{item.sellFor.map((price, index) => (
										<li key={`${item.id}-sell-${index}`}>
											{price.price} {price.currency} ({price.priceRUB} RUB) - {price.source}
										</li>
									))}
								</ul>
							</>
						)}
						{item.buyFor && item.buyFor.length > 0 && (
							<>
								<p>Buy For:</p>
								<ul className="price-list">
									{item.buyFor.map((price, index) => (
										<li key={`${item.id}-buy-${index}`}>
											{price.price} {price.currency} ({price.priceRUB} RUB) - {price.source}
										</li>
									))}
								</ul>
							</>
						)}
						<p>Base Price: {item.basePrice}</p>
						<p>Avg 24h Price: {item.avg24hPrice}</p>
						<img src={item.gridImageLink} alt={item.name} className="item-image" />
						<a href={item.wikiLink} target="_blank" rel="noopener noreferrer" className="wiki-link">
							Wiki Link
						</a>
						<ul className="task-list">
							{item.usedInTasks.map((task) => (
								<li key={`${item.id}-task-${task.id}`}>
									Task: {task.name}, Min Player Level: {task.minPlayerLevel}
								</li>
							))}
						</ul>
					</div>
				))}
			</ul>
		</div>
	);
}

export default App;
